mapper.select:
select *,
	DATE_FORMAT(award_date, '%Y') AS year,
	DATE_FORMAT(award_date, '%c') AS month
	from award 
	where locale like #{locale}
order by award_date DESC;

select distinct
	DATE_FORMAT(award_date, '%Y') AS year
	from award
	where locale = #{locale}
	order by award_date DESC;

select distinct
	DATE_FORMAT(award_date, '%Y') AS year,
	DATE_FORMAT(award_date, '%c') AS month
	from award
	where locale = #{locale}
	order by award_date desc;

mapper.update:
UPDATE award SET 
	award_date=#{awardDate}, 
	content=#{content},

	img_url=#{imgUrl},

	host = #{host}
WHERE award_no = #{awardNo}
