mapper.select:
SELECT @rownum:=@rownum+1 as rownum, p.post_no, p.post_asc, p.post_title,
date_format(POST_DATE, '%Y-%m-%d') post_date, p.post_origin_no, p.board_no, p.member_no, p.member_name FROM board b, post p, member m, (select @rownum:=0) row_reset WHERE b.board_no = p.board_no AND m.member_no = p.member_no AND b.board_no = #{boardNo}
AND p.post_title like concat ('%',#{comm.searchKeyword},'%')
AND p.member_name like concat ('%',#{comm.searchKeyword},'%')
ORDER BY p.post_asc DESC LIMIT #{limit} OFFSET #{offset}

SELECT @rownum:=@rownum+1 as rowNo, p.post_no, p.post_asc, p.post_title, p.post_contents, date_format(p.POST_DATE, '%Y-%m-%d') post_date, p.post_origin_no, p.board_no, p.member_no, p.member_name FROM board b, post p, member m, (select @rownum:=0) row_reset WHERE b.board_no = p.board_no AND m.member_no = p.member_no AND b.board_no = #{boardNo}
AND p.post_title like concat ('%',#{comm.searchKeyword},'%')
AND p.member_name like concat ('%',#{comm.searchKeyword},'%')
ORDER BY p.post_asc DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE board

	board_title = #{boardTitle},
	board_type = #{boardType},
	board_category = #{boardCategory},
	board_email = #{boardEmail} 

WHERE board_no = #{boardNo : NUMERIC}

UPDATE post

	post_title = #{postTitle},
	post_Contents = #{postContents},
	post_origin_no = #{postOriginNo}

WHERE post_no = #{postNo}

UPDATE post
POST_ASC = #{postNo}, post_origin_no = #{postNo},
WHERE post_no = #{postNo}

UPDATE board_option
option_notice = #{optionNotice}, option_comment = #{optionComment}, option_secret = #{optionSecret}, option_answer = #{optionAnswer}, option_captcha = #{optionCaptcha}, option_mass = #{optionMass}, option_order = #{optionOrder}, option_addinfo = #{optionAddinfo}, option_popup = #{optionPopup}
WHERE board_no = #{optionBoard : NUMERIC}
