mapper.select:
SELECT *
FROM menu
WHERE code = #{code}
AND	revision_code = #{revision_code}

SELECT *
FROM menu
WHERE revision_code = 0
AND   origin_no = #{menu.code}
AND   code != 1
AND   ORD = #{menu.ord}
AND   locale = #{locale}
ORDER BY MENU_ASC ASC, CODE ASC

mapper.update:
UPDATE menu
CATEGORY_CODE = #{category_code}, REVISION_CODE = #{revision_code}, TITLE = #{title : VARCHAR}, CONTENT = #{content : VARCHAR}, URL =#{url : VARCHAR}, STATUS = #{status : VARCHAR},
WHERE CODE = #{code:NUMERIC}

UPDATE head
TITLE = #{title : VARCHAR}, STATUS = #{status : VARCHAR}, CONTENT = #{content : VARCHAR},
HEAD_IMAGE = #{headImage : VARCHAR},
HEAD_URL = #{headUrl : VARCHAR}
WHERE HEAD_NO = #{headNo : NUMERIC}

UPDATE bottom

	CONTENT = #{content : VARCHAR}

WHERE BOTTOM_NO = 1
