mapper.select:
select h.*
 from (select @ROWNUM := @ROWNUM +1 AS ROWNUM,
			  H.*
		 from history H,(SELECT @ROWNUM := 0) TMP
		 where locale like #{locale}
	    order by history_date DESC ) h
LIMIT #{limit} OFFSET #{offset}

SELECT p.* FROM(SELECT @rownum:=@rownum+1 as rownum, tb1.partner_no, tb1.partner_name, tb1.partner_image, date_format(tb1.in_dtm, '%Y-%m-%d') in_dtm FROM partner tb1, (select @rownum:=0) row_reset WHERE 1=1
AND tb1.partner_name like concat ('%',#{searchKeyword},'%')
) p LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM(SELECT @rownum:=@rownum+1 as rownum, tb1.partner_no, tb1.partner_name, tb1.partner_image, date_format(tb1.in_dtm, '%Y-%m-%d') in_dtm FROM partner tb1, (select @rownum:=0) row_reset WHERE 1=1
AND tb1.partner_name like concat ('%',#{searchKeyword},'%')
) countTable

mapper.update:
UPDATE partner

	PARTNER_NAME = #{partnerName}, 
	PARTNER_IMAGE = #{partnerImage}

WHERE PARTNER_NO = #{partnerNo}
